// composables/useCounter.ts
// composables 下模块的同名导出将被自动按需引入
export const useCounter = () => useState('counter', () => 100)

const modules = import.meta.globEager(
    '/src/modules/**/*.js'
)

console.log(modules) // 将以对象的方式输出到前端控制台

// src/modules/pinia.js
import { createPinia } from 'pinia'

export default app => app.use(createPinia())

import App from './App.vue'
import { createApp } from 'vue'

const app = createApp(App)

// 引入所有模块
const modules = import.meta.globEager(
    '/src/modules/**/*.js'
)
// 安装插件
Object.values(modules).forEach(module => {
    if (typeof module.default === 'function') {
        module.default(app)
    }
})

app.mount('#app')

// src/stores/counter.js
import { defineStore } from 'pinia'

export const useCounterStore = defineStore('counter', {
    state() {
        return {
            counter: 0
        }
    },
    actions: {
        inc() {
            this.counter++
        }
    }
})

// plugins/foo.js
// 虚拟模块 id
const virtualModuleId = 'msg'
// 处理之后的虚拟模块 id
const resolvedVirtualModuleId = '\0' + virtualModuleId
// 虚拟模块代码
const code = `export default 100`

export default {
    name: 'foo',
    resolveId(id) {
        if (id === virtualModuleId) {
            return resolvedVirtualModuleId
        }
    },
    load(id) {
        if (id === resolvedVirtualModuleId) {
            return code
        }
    }
}

